    return api.put(`/quizzes/${id}`, transformedData);
  },
  
  deleteQuiz(id) {
    return api.delete(`/quizzes/${id}`);
  },

  // –†–∞–±–æ—Ç–∞ —Å –≤–æ–ø—Ä–æ—Å–∞–º–∏
